(function() {
    'use strict';

    var hbs = require("hbs");

    hbs.registerHelper({
        add: add,
        compare: compare,
        or: or
    });

    /*******************************************
    Impelementation
    *******************************************/
    function add(a, b) {
        return a + b;
    }

    function compare(left, operator, right) {

        if (arguments.length < 3) {
            throw new Error('Handlebars Helper "compare" needs 3 parameters');
        }

        if ( operator === '===' ) {
            return left === right;
        }
        else if ( operator === '!==' ) {
            return left !== right;
        }

        throw new Error('Handlebars Helper "compare" doesn\'t know the operator ' + operator);
    }

    function or ( a, b ) {
        return a || b;
    }

}());
